                  })}
              </Row>
            </Col>
          </Row>
        )}
      </TabPanel>
      <TabPanel value={value} index={2}>
        {showLoader ? (
          <Box
            sx={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <CircularProgress />
          </Box>
        ) : (
          <Row>
            <Col>
              {showLoader ? (
                <Box
                  sx={{
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                  }}
                >
                  <CircularProgress />
                </Box>
              ) : (
                <Row>
                  <Col>
                    <Row sm={12} md={6} lg={4} w={100} className="techItems">
                      {technologies
                        .filter((tech) => tech.category === "Fertilizer")
                        .map(({ title, picture, price, des, _id }) => {
                          return (
                            <Card
                              className="Ecard"
                              // style={{ width: "15rem", height: "20rem" }}
                            >
                              <Link to={`/digitalTechnologies/${_id}`}>
                                <Card.Img
                                  variant="top"
                                  className="Eimages"
                                  src={picture}
                                />
                                <Card.Body>
                                  <Card.Title
                                    style={{ color: "black", fontSize: "22px" }}
                                  >
                                    {title}
                                  </Card.Title>
                                  <Card.Text
                                    style={{ color: "#666", lineHeight: 1.6 }}
                                  >
                                    {des.slice(0, 90)}...
                                  </Card.Text>
                                  <Card.Text
                                    style={{ color: "#333", fontSize: "18px" }}
                                  >
                                     {price}
                                  </Card.Text>
                                </Card.Body>
                              </Link>
                            </Card>
                          );
                        })}
                    </Row>
                  </Col>
                </Row>
              )}
            </Col>
          </Row>
        )}
      </TabPanel>
      <TabPanel value={value} index={3}>
        {showLoader ? (
          <Box
            sx={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <CircularProgress />
          </Box>
        ) : (
          <Row>
            <Col>
              <Row sm={12} md={6} lg={4} w={100} className="techItems">
                {technologies
                  .filter((tech) => tech.category === "WaterPump")
                  .map(({ title, picture, price, des, _id }) => {
                    return (
                      <Card
                        className="Ecard"
                        // style={{ width: "15rem", height: "20rem" }}
                      >
                        <Link to={`/digitalTechnologies/${_id}`}>
                          <Card.Img
                            variant="top"
                            className="Eimages"
                            src={picture}
                          />
                          <Card.Body>
                            <Card.Title
                              style={{ color: "black", fontSize: "22px" }}
                            >
                              {title}
                            </Card.Title>
                            <Card.Text
                              style={{ color: "#666", lineHeight: 1.6 }}
                            >
                              {des.slice(0, 90)}...
                            </Card.Text>
                            <Card.Text
                              style={{ color: "#333", fontSize: "18px" }}
                            >
                               {price}
                            </Card.Text>
                          </Card.Body>
                        </Link>
                      </Card>
                    );
                  })}
              </Row>
            </Col>
          </Row>
        )}
      </TabPanel>
      <TabPanel value={value} index={4}>
        {showLoader ? (
          <Box
            sx={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <CircularProgress />
          </Box>
        ) : (
          <Row>
            <Col>
              <Row sm={12} md={6} lg={4} w={100} className="techItems">
                {technologies
                  .filter((tech) => tech.category === "Pipes")
                  .map(({ title, picture, price, des, _id }) => {
                    return (
                      <Card
                        className="Ecard"
                        // style={{ width: "15rem", height: "20rem" }}
                      >
                        <Link to={`/digitalTechnologies/${_id}`}>
                          <Card.Img
                            variant="top"
                            className="Eimages"
                            src={picture}
                          />
                          <Card.Body>
                            <Card.Title
                              style={{ color: "black", fontSize: "22px" }}
                            >
                              {title}
                            </Card.Title>
                            <Card.Text
