import React, { useEffect } from 'react';
import { useForm } from "react-hook-form";
import { Link, useHistory } from "react-router-dom";
import useAuth from '../../hooks/useAuth'
import {useState} from 'react';
import {Button, Col, Container, Row} from "react-bootstrap";


const SignUp = () => {
   const { user,setUser,registerUser,setIsLoading} = useAuth();
      const history = useHistory();
      const [error, setError] = useState(false);
      const [passError, setPassError] = useState(false);
      const {
        register,
        handleSubmit,
        reset,
        // formState: { errors },
      } = useForm();

      //  adding user to database after login..should call this after succesfull sign up

function saveUser (name,eamil,mobile,nidNo){

  const url = "http://127.0.0.1:8000/SaveUser";
