import React, {Component, Fragment} from 'react';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import {Col, Container, Row, Slider,} from "react-bootstrap";


;

class ClientReview extends Component {
    render() {
        var settings = {
            autoPlay: true,
            autoplaySpeed: 1000,
            dots: true,
            infinite: true,
            speed: 1000,
            vertical: true,
            verticalSwiping: true,
            slidesToShow: 1,
            slidesToScroll: 1,

            responsive: [
                {
                    breakpoint: 1024,
                    settings: {
                        slidesToShow: 1,
                        slidesToScroll: 1,
                        infinite: true,
                        dots: true
                    }
                },
                {
                    breakpoint: 600,
                    settings: {
                        slidesToShow: 1,
                        slidesToScroll: 1,
                        initialSlide: 1
                    }
                },
                {
                    breakpoint: 480,
                    settings: {
                        slidesToShow: 1,
                        slidesToScroll: 1
                    }
                }
            ]
        };


        return (
            <Fragment>
                <Container className="text-center">
                    <h1 className="serviceMainTitle">CLIENT SAYS</h1>

                    <Slider>
                        <div>
                            <Row className="text-center">
                                <Col>
                                    <img className="circleImg" src="riad.jpg"/>
                                    <h1>Web Development</h1>
                                    <p>My name is Rifatul islam Riad
                                        My name is Rifatul islam Riad
                                        My name is Rifatul islam Riad
                                        My name is Rifatul islam Riad
                                        My name is Rifatul islam Riad
                                    </p>
                                </Col>

                            </Row>
                        </div>


                    </Slider>
                </Container>

            </Fragment>
        );
    }
}

export default ClientReview;
