import React, {Component, Fragment} from 'react';
import whiteLogo from '../../asset/image/navlogo.svg';
import blueLogo from '../../asset/image/navlogoscroll.svg'
import '../../asset/css/custom.css'
import '../../asset/css/bootstrap.min.css';
import {NavLink} from "react-router-dom"
import {Container, Nav, Navbar,} from "react-bootstrap";
import { useHistory } from 'react-router-dom';
import useAuth from '../../hooks/useAuth';
import { Button } from 'bootstrap';
import { useState } from 'react';


// navBarTitle: "navTitle",
// navBarLogo: [whiteLogo],
// navVariant: "dark",
// navBarBack: "navBackground",
// navBarItem: "navItem",
// pageTitle: props.title,
const Header = (props) => {

   
const { user, logOut } = useAuth();
const [navStyles,setNavStyles]= useState({knavBarTitle:"navTitle",navBarLogo:[whiteLogo],navVariant:"dark",navBarBack:"navBackround",navBarItem:"navItem"})




const onScrollNavbar = () => {
    if (window.scrollY >100) {
        setNavStyles({navBarTitle:"navTitleScroll",navBarLogo:[blueLogo],navVariant:"light",navBarBack:"navBackgroundScroll",navBarItem:"navItemScroll"})
      
    } else if(window.scrollY <100){
      
        setNavStyles({navBarTitle:"navTitle",navBarLogo:[whiteLogo],navVariant:"dark",navBarBack:"navBackround",navBarItem:"navItem"})
     
    }
  };
//   handlelogout
const handleLogout=()=>{
if(user?.email){
    logOut();
}

}
  // change navbar on scroll
  window.addEventListener("scroll", onScrollNavbar);
    return (
        <Fragment>
            <title>{props.title}</title>
            <Navbar variant={navStyles.navVariant} className={navStyles.navBarBack} fixed={"top"} collapseOnSelect
                    expand="lg">
                <Container>
                    <Navbar.Brand><NavLink className={navStyles.navBarTitle} to="/"><img
                        src={navStyles.navBarLogo}/> R@ Real Estate
                        Company</NavLink></Navbar.Brand>
                    <Navbar.Toggle aria-controls="responsive-navbar-nav"/>
                    <Navbar.Collapse id="responsive-navbar-nav">
                        <Nav className="me-auto">

                        </Nav>
                        <Nav>
                            <Nav.Link><NavLink exact activeStyle={{color: '#0a8ee'}}
                                               className={navStyles.navBarItem}
                                               to="/">HOME</NavLink></Nav.Link>
                            <Nav.Link><NavLink exact activeStyle={{color: '#0a8ee'}}
                                               className={navStyles.navBarItem}
                                               to="/service">SERVICES</NavLink></Nav.Link>
                            <Nav.Link> <NavLink exact activeStyle={{color: '#0a8ee'}}
                                                className={navStyles.navBarItem}
                                                to="/sale">SALE</NavLink></Nav.Link>
                            <Nav.Link> <NavLink exact activeStyle={{color: '#0a8ee'}}
                                                className={navStyles.navBarItem}
                                                to="/rent">RENT</NavLink></Nav.Link>
                            <Nav.Link> <NavLink exact activeStyle={{color: '#0a8ee'}}
                                                className={navStyles.navBarItem}
                                                to="/contact">CONTACT</NavLink></Nav.Link>
                            <Nav.Link> <NavLink exact activeStyle={{color: '#0a8ee'}}
                                                className={navStyles.navBarItem}
                                                to="/about">ABOUT</NavLink></Nav.Link>
                        
                           {!user?.email&&
    <>
     <Nav.Link> <NavLink exact activeStyle={{color: '#0a8ee'}}
                         className={navStyles.navBarItem}
                         to="/login">LOGIN</NavLink></Nav.Link>
     <Nav.Link> <NavLink exact activeStyle={{color: '#0a8ee'}}
                         className={navStyles.navBarItem}
                         to="/registration">REGISTRATION</NavLink></Nav.Link>
    </>
    }
                        {
                            user?.email&&
                      <>
                            <Nav.Link> <NavLink exact activeStyle={{color: '#0a8ee'}}
                                                className={navStyles.navBarItem}
                                                to="/dashboard">DASHBOARD</NavLink></Nav.Link>
                    <button class="btn btn-outline-primary" onClick={()=>handleLogout()}>Logout</button>
                      </>
                        }

                        </Nav>
                    </Navbar.Collapse>
                </Container>
            </Navbar>

        </Fragment>
    )
};

export default Header;
