import React, { useEffect } from 'react';
import { useForm } from "react-hook-form";
import { Link, useHistory } from "react-router-dom";
import useAuth from '../../hooks/useAuth'
import {useState} from 'react';
import {Button, Col, Container, Row} from "react-bootstrap";
import RestClient from '../../RestAPI/RestClient';
import AppUrl from '../../RestAPI/AppUrl';


const SignUp = () => {
   const { user,setUser,registerUser,setIsLoading} = useAuth();
      const history = useHistory();
      const [error, setError] = useState(false);
      const [passError, setPassError] = useState(false);
      const {
        register,
        handleSubmit,
        reset,
        // formState: { errors },
      } = useForm();

// Adding users after signing up to web page
function saveUser(name,email,mobile,nidNo){
  const user = {
name:name,
email:email,
mobile:mobile,
nidNo:nidNo
  }
  const url = "http://127.0.0.1:8000/SaveUser";

fetch(url,{
  mode:"no-cors",
  method:"POST",
  body:JSON.stringify(user),
  headers: {
    "Content-type": "application/json"
},


})
.then(res=>res.json())
.then(res=>console.log(res.data))
.catch(err=>console.log(err.response))
}

    //   show user
    useEffect(()=>{
    console.log(user)
    console.log(user.email,user.displayName,user.mobile,user.nidNo)
    },[user])
    
      const onSubmit = (data) => {
        //   console.log(data)
        setError(false);
        setPassError(false);
        if (
          data.name === "" ||
          data.email === "" ||
          data.password === "" ||
          data.mobile === "" ||
          data.nidNo === "" ||
          data.remember===""
        ) {
          setError(true);
          return;
        }
         // console.log(data.email, data.password);
    
        // register user with google
        registerUser(data.email, data.password)
          .then((result) => {
            setUser({ ...user, email: data.email, displayName: data.name,mobile:data.mobile,nidNo: data.nidNo });
           alert("Registration successfull")
            history.push("/");
            saveUser(data.name,data.email,data.mobile,data.nidNo)
         
          })
          .catch((err) => {
            alert("Something went wrong")
          })
          .finally(setIsLoading(false));
    
       
        reset();

       
        }
       
             
return (
    <>
        <Container fluid={true} className="topFixedPage p-4">
            <div>
                <Container className="regBox">
                    <Row>

                        {/* <Col lg={6} md={6} sm={12}> */}
                            <form  noValidate
              autoComplete="off"
              onSubmit={handleSubmit(onSubmit)}>

                                <h3 className="logIn">Sign Up</h3>

                                <div className="form-group">
                                    <label className="loginText">Name</label>
                                    <input  {...register("name")} type="text" className="form-control" placeholder="Enter name"/>
                                </div>
                                <div className="form-group">
                                    <label className="loginText">Email</label>
                                    <input  {...register("email")} email type="email" className="form-control" placeholder="Enter email"/>
                                </div>
                                <div className="form-group">
                                    <label className="loginText">Mobile No</label>
                                    <input {...register("mobile")} type="text" className="form-control" placeholder="Enter mobile no"/>
                                </div>
                                <div className="form-group">
                                    <label className="loginText">National ID</label>
                                    <input {...register("nidNo")} type="text" className="form-control" placeholder="Enter nid no"/>
                                </div>

                                <div className="form-group">
                                    <label className="loginText">Password</label>
                                    <input  {...register("password")} type="password" className="form-control"
                                           placeholder="Enter password"/>
                                </div>

                                <div className="form-group">
                                    <div className="custom-control custom-checkbox">
                                        <input  {...register("remember")} type="checkbox" className="custom-control-input"
                                               id="customCheck1"/>
                                        <label className="loginText2" htmlFor="customCheck1">Remember
                                            me</label>
                                    </div>
                                </div>

                                <Button variant="success" type="submit" >Sign Up</Button>
                                {error && (
                <p style={{ color: "red", letterSpacing: "1px",marginTop:"5px" }}>
                  input fields cannot be empty
                </p>
              )}
                                <p className="loginText2">
                                    Forgot, <a className="loginText" href="#">password?</a>
                                </p>
                            </form>
                        {/* </Col> */}


                    </Row>
                </Container>
            </div>


        </Container>


    </>
);
      };


 
   


export default  SignUp;
